                                                                                                                                                                                                                                                        .orbit-container {
                                                                                                                                                                                                                                                          position: relative;
                                                                                                                                                                                                                                                            width: 250px;
                                                                                                                                                                                                                                                              height: 250px;
                                                                                                                                                                                                                                                                display: flex;
                                                                                                                                                                                                                                                                  justify-content: center;
                                                                                                                                                                                                                                                                    align-items: center;
                                                                                                                                                                                                                                                                      margin-top: 000px;  /* ➜ shu bilan pastga suriladi */
                                                                                                                                                                                                                                                                      }

                                                                                                                                                                                                                                                                          #crashValue {
                                                                                                                                                                                                                                                                                position: absolute;
                                                                                                                                                                                                                                                                                      font-size: 2.2em;
                                                                                                                                                                                                                                                                                            font-weight: 900;
                                                                                                                                                                                                                                                                                                  color: red;
                                                                                                                                                                                                                                                                                                        text-align: center;
                                                                                                                                                                                                                                                                                                            }

                                                                                                                                                                                                                                                                                                                canvas {
                                                                                                                                                                                                                                                                                                                      position: absolute;
                                                                                                                                                                                                                                                                                                                            width: 100%;
                                                                                                                                                                                                                                                                                                                                  height: 100%;
                                                                                                                                                                                                                                                                                                                                      }

                                                                                                                                                                                                                                                                                                                                         #signalStatus {
                                                                                                                                                                                                                                                                                                                                           position: absolute;
                                                                                                                                                                                                                                                                                                                                             bottom: 180px;         
                                                                                                                                                                                                                                                                                                                                               left: 50%;
                                                                                                                                                                                                                                                                                                                                                 transform: translateX(-50%);
                                                                                                                                                                                                                                                                                                                                                   background: red; /* Yopiq qizil */
                                                                                                                                                                                                                                                                                                                                                     box-shadow: 0 0 10px #FF3B3B, 0 0 20px #FF3B3B; /* Edge glow */
                                                                                                                                                                                                                                                                                                                                                       color: white; /* Matn ravshan ko‘rinsin */           
                                                                                                                                                                                                                                                                                                                                                         font-size: 22px;       
                                                                                                                                                                                                                                                                                                                                                           font-weight: 900;
                                                                                                                                                                                                                                                                                                                                                             padding: 15px 20px;    
                                                                                                                                                                                                                                                                                                                                                               min-width: 120px;
                                                                                                                                                                                                                                                                                                                                                                 min-height: 10px;
                                                                                                                                                                                                                                                                                                                                                                   border-radius: 10px;
                                                                                                                                                                                                                                                                                                                                                                     text-align: center;
                                                                                                                                                                                                                                                                                                                                                                       z-index: 9999;
                                                                                                                                                                                                                                                                                                                                                                         display: flex;
                                                                                                                                                                                                                                                                                                                                                                           justify-content: center;
                                                                                                                                                                                                                                                                                                                                                                             align-items: center;

                                                                                                                                                                                                                                                                                                                                                                             }
                                                                                                                                                                                                                                                                                                                                                                               </style>
                                                                                                                                                                                                                                                                                                                                                                               </head>
                                                                                                                                                                                                                                                                                                                                                                               <body>

                                                                                                                                                                                                                                                                                                                                                                                <div class="top-bar">
                                                                                                                                                                                                                                                                                                                                                                                  <span id="animatedText">BYTROX PREDICTOR</span>
                                                                                                                                                                                                                                                                                                                                                                                  </div>

                                                                                                                                                                                                                                                                                                                                                                                  <script>
                                                                                                                                                                                                                                                                                                                                                                                  const texts = [
                                                                                                                                                                                                                                                                                                                                                                                    "BYTROX PREDICTOR",
                                                                                                                                                                                                                                                                                                                                                                                      "https://t.me/BYTROX_NOVA",
                                                                                                                                                                                                                                                                                                                                                                                        "CONTRACT @BYTROX_1"
                                                                                                                                                                                                                                                                                                                                                                                        ];

                                                                                                                                                                                                                                                                                                                                                                                        let currentIndex = 0;
                                                                                                                                                                                                                                                                                                                                                                                        const animatedText = document.getElementById("animatedText");

                                                                                                                                                                                                                                                                                                                                                                                        function showNextText() {
                                                                                                                                                                                                                                                                                                                                                                                          // Fade out
                                                                                                                                                                                                                                                                                                                                                                                            animatedText.style.transition = "opacity 1s";
                                                                                                                                                                                                                                                                                                                                                                                              animatedText.style.opacity = 0;

                                                                                                                                                                                                                                                                                                                                                                                                setTimeout(() => {
                                                                                                                                                                                                                                                                                                                                                                                                    // Keyingi textni o'rnatish
                                                                                                                                                                                                                                                                                                                                                                                                        currentIndex = (currentIndex + 1) % texts.length;
                                                                                                                                                                                                                                                                                                                                                                                                            animatedText.innerText = texts[currentIndex];

                                                                                                                                                                                                                                                                                                                                                                                                                // Fade in
                                                                                                                                                                                                                                                                                                                                                                                                                    animatedText.style.opacity = 1;

                                                                                                                                                                                                                                                                                                                                                                                                                        // 3 sekunddan keyin keyingi textga o‘tish
                                                                                                                                                                                                                                                                                                                                                                                                                            setTimeout(showNextText, 4000);
                                                                                                                                                                                                                                                                                                                                                                                                                              }, 1000); // Fade-out duration
                                                                                                                                                                                                                                                                                                                                                                                                                              }

                                                                                                                                                                                                                                                                                                                                                                                                                              // Animatsiyani boshlash
                                                                                                                                                                                                                                                                                                                                                                                                                              setTimeout(showNextText, 4000); // Birinchi text 3 sekund turadi
                                                                                                                                                                                                                                                                                                                                                                                                                              </script>


                                                                                                                                                                                                                                                                                                                                                                                                                                <div id="signalStatus">SIGNAL OFF</div>

                                                                                                                                                                                                                                                                                                                                                                                                                                  <div class="orbit-container">
                                                                                                                                                                                                                                                                                                                                                                                                                                    <img src="https://i.postimg.cc/nh8pLMr5/Picsart-25-09-27-16-57-08-004.png" class="avi-img">
                                                                                                                                                                                                                                                                                                                                                                                                                                    <canvas id="orbitCanvas"></canvas>
                                                                                                                                                                                                                                                                                                                                                                                                                                        <div id="crashValue">0.00</div>
                                                                                                                                                                                                                                                                                                                                                                                                                                          </div>

                                                                                                                                                                                                                                                                                                                                                                                                                                            <div class="bottom-bar">
                                                                                                                                                                                                                                                                                                                                                                                                                                                <img id="menuIcon" class="circle-icon" src="https://i.postimg.cc/3JQxyVMK/menu.png" alt="Menu">
                                                                                                                                                                                                                                                                                                                                                                                                                                                    <div id="time">00:00 AM</div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                        <img id="tgImg" class="circle-icon" src="https://i.postimg.cc/9FPFybVy/tg.jpg" alt="Telegram">
                                                                                                                                                                                                                                                                                                                                                                                                                                                          </div>

                                                                                                                                                                                                                                                                                                                                                                                                                                                          <script>
                                                                                                                                                                                                                                                                                                                                                                                                                                                            let crashElement = document.getElementById("crashValue");
                                                                                                                                                                                                                                                                                                                                                                                                                                                              let signalStatus = document.getElementById("signalStatus");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                let socket = null;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                  function setSignal(active) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                      if (active) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            signalStatus.innerHTML = 'SIGNAL ON <img src="https://i.postimg.cc/vHxPnnrB/Picsart-25-09-26-23-17-43-010.png" style="width:27px;height:27px;margin-left:5px;vertical-align:middle;">';
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  signalStatus.style.background = "red"; // Yopiq qizil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  signalStatus.style.boxShadow = "0 0 10px #FF3B3B, 0 0 20px #FF3B3B"; // Edge glow
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  signalStatus.style.color = "white"; // Matn ravshan ko‘rinsin
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            signalStatus.innerText = "SIGNAL OFF";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  signalStatus.style.background = "red"; // Yopiq qizil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  signalStatus.style.boxShadow = "0 0 10px #FF3B3B, 0 0 20px #FF3B3B"; // Edge glow
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  signalStatus.style.color = "white"; // Matn ravshan ko‘rinsin
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          function connectWebSocket() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              socket = new WebSocket("wss://1x-bet.mobi/games-frame/sockets/crash?ref=1&gr=54&whence=110&fcountry=192&appGuid=games-web-app-default-web-v2&lng=en&v=1.5");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  socket.onopen = function () {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        setSignal(true);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              socket.send('{"protocol":"json","version":1}\x1e');
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    socket.send('{"arguments":[{"activity":30,"currency":119}],"invocationId":"0","target":"Guest","type":1}\x1e');
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        };
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            socket.onmessage = function (event) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  try {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          let data = event.data.slice(0, -1);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  let json = JSON.parse(data);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          if (json.target === "OnCrash") {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    let value = json.arguments[0].f;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              crashElement.innerText = value;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            } catch {}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                };
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    socket.onclose = function () {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          setSignal(false);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                setTimeout(connectWebSocket, 3000);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    };
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        socket.onerror = function () {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              setSignal(false);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  };
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      connectWebSocket();

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        const canvas = document.getElementById("orbitCanvas");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          const ctx = canvas.getContext("2d");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            let w, h;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              function resize() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  w = canvas.width = canvas.offsetWidth;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      h = canvas.height = canvas.offsetHeight;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          window.addEventListener("resize", resize);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            resize();

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              let animFrac = 0;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                const dashOn = 9494949.9999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999, dashOff = 0.9;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  const strokeColor = "#FF3B3B"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    const planeCountOuter= 6, planeCountInner = 6;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      const planeImg = new Image();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        planeImg.src = "https://i.postimg.cc/Mpd6FGnT/plane.png"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          function drawPlane(x, y, angle, scale) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ctx.save();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ctx.translate(x, y);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ctx.rotate(angle);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          ctx.scale(scale, scale);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ctx.drawImage(planeImg, -49, -65.1155151516616161, 100, 100);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ctx.restore();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      function drawOrbit(radius, count, cw) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          ctx.beginPath();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              ctx.arc(w/2, h/2, radius, 0, Math.PI*2);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  ctx.stroke();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      for (let i = 0; i < count; i++) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            let base = i / count;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  let frac = cw ? (base + animFrac) : (base - animFrac);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        frac -= Math.floor(frac);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              let angle = frac * Math.PI * 2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    let x = w/2 + Math.cos(angle) * radius;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          let y = h/2 + Math.sin(angle) * radius;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                drawPlane(x, y, angle, Math.min(w,h)/560);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        function draw() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ctx.clearRect(0,0,w,h);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ctx.strokeStyle = strokeColor;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ctx.lineWidth = Math.max(2, Math.min(w,h)*0.01);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ctx.setLineDash([dashOn, dashOff]);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            ctx.lineDashOffset = animFrac * 1000;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                let rOuter = Math.min(w,h) * 0.42;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    let rInner = Math.min(w,h) * 0.28;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        drawOrbit(rOuter, planeCountOuter, false);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            drawOrbit(rInner, planeCountInner, true);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                animFrac += 0.0040;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    requestAnimationFrame(draw);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        draw();

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        function updateTime() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            let now = new Date();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                let hours = now.getHours();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    let minutes = now.getMinutes();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        let seconds = now.getSeconds();
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            let ampm = hours >= 12 ? "PM" : "AM";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                hours = hours % 12;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    hours = hours ? hours : 12;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        let str =
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              String(hours).padStart(2,"0")+":"+
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    String(minutes).padStart(2,"0")+":"+
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          String(seconds).padStart(2,"0")+" "+ampm;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              document.getElementById("time").innerText = str;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  setInterval(updateTime, 1000);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    updateTime();


                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      document.getElementById("tgImg").onclick = function() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          window.open("https://t.me/bytrox_1", "_blank");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            };
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              document.getElementById("menuIcon").onclick = function() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  window.location.href = "https://bytroxx.zya.me/server/game.select.-site.php"
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    };
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    </script>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    </body>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    </html>
